// module app

(function(){

  // controller chatBot.controllers.chat

  (function(){

    abp.utils.createNamespace(window, 'chatBot.controllers.chat');

    chatBot.controllers.chat.ask = function(input, ajaxParams) {
      return abp.ajax($.extend(true, {
        url: abp.appPath + 'api/chat/ask',
        type: 'POST',
        data: JSON.stringify(input)
      }, ajaxParams));
    };

  })();

  // controller chatBot.services.openAi

  (function(){

    abp.utils.createNamespace(window, 'chatBot.services.openAi');

    chatBot.services.openAi.askChatGpt = function(messagesThread, ajaxParams) {
      return abp.ajax($.extend(true, {
        url: abp.appPath + 'api/app/openai/ask-chat-gpt',
        type: 'POST',
        data: JSON.stringify(messagesThread)
      }, { dataType: 'text' }, ajaxParams));
    };

  })();

})();


